    let combatTurns = 0;
    let fighter1RemainingHealth = Fighter1.getHealth();
    let fighter2RemainingHealth = Fighter2.getHealth();
    let fighter1Damage = 0;
    let fighter2Damage = 0;

    // if (typeof(Fighter1) === typeof(Fighter2)) {
